CREATE TABLE bank (
    id INT PRIMARY KEY,
    bank_name VARCHAR(255) NOT NULL,
    address VARCHAR(255) NOT NULL,
    email VARCHAR(255) UNIQUE,
    ifsc_code VARCHAR(11) NOT NULL,
    branch VARCHAR(255),
	city VARCHAR(255),
    fund DECIMAL(18, 2) DEFAULT 0,
	UNIQUE(ifsc_code,address)
);

select * from bank;



